var classy = require('classy')

module.exports = classy.defineMixin({

    $after: {
        appendStateProperties: function(props){
            props.push('columns')
        }
    },

    $before: {
        prepareColumns: function(columns){

            if (!Array.isArray(columns)){
                columns = [].slice.call(arguments, 0)
            }

            this.columns = columns
        }
    }
})