module.exports = require('./defineStatement')('delete', {

	init: function(what){
		this.callSuper()
		this.target = what
	},

	toString: function(){

		var from  = this.fromToString()
		var where = this.whereToString()

		return 'DELETE ' + from + where
	},

	appendStateProperties: function(props){
	    props.push('target', 'whereConditions')
	},

	fromToString: function(){
		var target = ''

		if (typeof this.target == 'string'){
			target = this.target
		} else {
			target = this.target.name
		}

		return 'FROM ' + target
	},

	whereToString: require('./whereToString'),
	where        : require('./where')
})