module.exports = require('./defineStatement')('delete', {

	init: function(){
		this.callSuper()
	},

	toString: function(){

		var what = this.whatToString()

		return 'DROP ' + what
	},

	appendStateProperties: function(props){
	    props.push('target')
	},

	table: function(target){
		this.name   = 'TABLE'
		this.target = target

		return this
	},

	targetToString: function(){
		return typeof this.target == 'string'?
						this.target:
						this.target.name?
							this.target.name:
							this.target.toString()
	},

	whatToString: function(){
		return this.name + ' ' + this.targetToString()
	}
})