'use strict'

var SchemaElement   = require('./SchemaElement')
var classy       = require('classy')
var toUpperFirst = require('ustring').toUpperFirst

module.exports = function(type, config){

    if (arguments.length < 2){
        return console.error('Please provide a type for the SchemaElement!')
    }

    config = config || {}

    var extend = config.extend

    if (typeof extend == 'string'){
        extend = require('./' + toUpperFirst(extend))
    }

    config.extend = extend || SchemaElement
    config.alias  = config.alias  || 'schema.' + toUpperFirst(type)
    config.schemaType   = type

    return classy.define(config)
}
