'use strict'

var classy = require('classy')

var Type = classy.define({

    alias : 'sql.BaseType',
    extend: require('../SqlBase'),

    statics: {

        fromState: function(state){
            var result = new this()

            result.copy(state)

            return result
        },

        toString: function(){
            return this.prototype.type
        },

        toJSON: function(){
            return {
                type: this.toString()
            }
        }
    },

    toString: function(){
        return this.type
    },

    toJSON: function(){
        return {
            type : this.type,
            value: this.toString()
        }
    }
})

module.exports = Type