var defineType = require('./defineType')

module.exports = defineType({

    type: 'string',

    statics: {
        displayName: 'StringType',
        defaultLength: 50,
        toString: function(){
            return 'varchar(' + StringType.defaultLength + ')'
        }
    },

    init: function(len){
        this.len = len
    },

    toString: function(){
        return 'varchar(' + this.len + ')'
    },

    toJSON: function(){
        var json = this.callSuper()

        json.len = this.len

        return json
    }

})