'use strict'

var classy       = require('classy')
var toUpperFirst = require('ustring').toUpperFirst
var Type         = require('./BaseType')

function define(config){
    config = config || {}

    config.extend = config.extend || Type
    config.alias  = 'sql.' + toUpperFirst(config.type)

    var NewType = classy.define(config)

    if (!config.statics || !config.statics.toString){
        NewType.toString = NewType.$superClass.toString
    }
    if (!config.statics || !config.statics.toJSON){
        NewType.toJSON = NewType.$superClass.toJSON
    }

    return NewType
}

module.exports = define