var Select = require('../Select')
var Types  = require('../Types')
var Table  = require('../Table')
var Column = require('../Column')
var Delete = require('../Delete')

describe('Delete', function(){

	var table = Table('users', null,
			Column('name', Types.String),
			Column('id', Types.String),
			Column('color', Types.String)
		)

	it('should delete where name and id', function(){

		var name = table.c.name
		var idCol = table.c.id

		Delete(table)
			.where('name').is('x')
			.and('id').is(5)
			.end()
			.toString()
			.should.equal('DELETE FROM users WHERE name = \'x\' AND id = 5')

	})
})