var Types  = require('../Types')

describe('GetType', function(){

    var getType = require('../sql/types/getType')

    it('should find string type', function(){

        getType({
            type: 'string'
        })
        .should
        .equal(Types.String)

    })

    it('should find json type', function(){

        getType({
            type: 'json'
        })
        .should
        .equal(Types.Json)

    })

    it('should find integer type', function(){

        getType({
            type: 'integer'
        })
        .should
        .equal(Types.Integer)

    })

    it('should find serial type', function(){

        getType({
            type: 'serial'
        })
        .should
        .equal(Types.Serial)

    })

    it('should find bigserial type', function(){

        getType({
            type: 'bigserial'
        })
        .should
        .equal(Types.BigSerial)
    })

})